import { createStore } from 'vuex'

export default createStore({
        state: {
            livros: [
                {
                    titulo: "O Senhor dos Aneis - A sociedade do anel",
                    genero: "Fantasia",
                    autor: "J.R.R. Tolkien",
                    editora: "HarperCollins",
                    descricao: " A Sociedade do Anel começa no Condado, a região rural do oeste da Terra-média onde vivem os diminutos e pacatos hobbits.",
                    data: "29/12/2020"
                },
                {
                    titulo: "IT - A Coisa",
                    genero: "Terror",
                    autor: "Stephen King",
                    editora: "Hodder",
                    descricao: "Seja guiado pelo poder do Anel Prístino e se torne um Lorde das Terras Intermédias.",
                    data: "14//2022"
                },
                {
                    titulo: "Chrono Trigger",
                    genero: "RPG",
                    autor: "Square Enix",
                    editora: "SNES",
                    descricao: "Viaje até o passado esquecido, o futuro distante e o fim dos tempos.",
                    data: "01/04/2022"
                },
            ]
        },
        getters: {
            getLivros: state => state.livros,
        },
        mutations: {
            addLivro: (state, newLivro) => state.livros.push(newLivro),
        },
        actions: {
            commitLivro: ({ commit }, newLivro ) => commit('addLivro', newLivro),
        },

    })
